Vue.component("qualification",{props:["JsonData"],data:function(){return{inputVal:"23",isInvalid:!0}},template:'  <div class="assessment-intro">  \n    <div class="cst-container">  \n     <div class="survey-intro">\n     <div class="introduction-title" v-html="JsonData.heading"></div>                       \n     <div v-if="isInvalid" class="validated-error"  >There is an error</div>\n     <div class="intro-panel"> \n         \n             <div v-for="(input, index) in JsonData.inputFields" class="col-50">\n                 <div class="qualification-inner">\n                     <label class="lbl-control" :for="\'qual_\'+ index" v-html="input.label"></label>                                        \n\n                     <input class="cst-form-control" v-if="input.inputType==\'text\'" :id="\'qual_\'+ index"\n                     @change="handleInputChange(input.inputId,index, $event)" :value="this.inputVal" @input="handleInput($event)" \n                     :placeholder="input.placeholder"/>\n\n                     <select class="cst-form-control" v-if="input.inputType==\'select\'"  :id="\'qual_\'+ index"\n                     @change="handleSelect(index,$event)" required>\n                         <option v-html="input.placeholder" value="" disabled selected></option>\n                         <option v-for="option in input.options" \n                         v-html="option.optionTxt" :value="option.optionId" :selected="input.selectedId==option.optionId">\n                         </option>\n                         </select>                     \n                         \n                        \n\n                     </div>\n                </div>\n            </div>\n        </div>\n     <div class="survey-begin txt-center">\n         <div class="btn-item frw" @click="handleForward" v-html="JsonData.frdBtnTxt"></div>\n     </div>\n     </div>\n </div>',methods:{handleInputChange:function(id,index,e){document.getElementById(id).value=e.target.value,this.JsonData.inputFields[index].inputVal=e.target.value,console.log(id)},handleSelect:function(index,e){this.JsonData.inputFields[index].selectedId=e.target.value},handleForward:function(){this.JsonData.inputFields.forEach(input=>{var val;if(!("select"==input.inputType?input.selectedId:"text"==input.inputType?input.inputVal:""))return this.isInvalid=!0,void this.$forceUpdate();console.log(this.isInvalid)})},handleInput:function(e){if(!/^[1-9]+[0-9]*$/.test(e.target.value)){var valArr=e.target.value.split("");valArr.pop(),e.target.value=valArr.join("")}}}});